    void Start()
    {
        GenerateFromColorData();
    }

    void GenerateFromColorData()
    {
        for (int y = 0; y < height; y++)
        {
            for (int x = 0; x < width; x++)
            {
                int index = y * width + x;
                uint pixel = pixelData[index];

                // Farbbestandteile extrahieren
                byte r = (byte)((pixel >> 16) & 0xFF);
                byte g = (byte)((pixel >> 8) & 0xFF);
                byte b = (byte)(pixel & 0xFF);
                byte a = (byte)((pixel >> 24) & 0xFF);

                Vector3 pos = new Vector3(x * cellSize, 0, -y * cellSize);

                Color32 color = new Color32(r, g, b, a);

                if (color.Equals(new Color32(0, 0, 0, 255)))
                {
                    Instantiate(wallPrefab, pos, Quaternion.identity, transform);
                }
                else if (color.Equals(new Color32(0, 0, 255, 255)))
                {
                    Instantiate(playerPrefab, pos + Vector3.up * 0.5f, Quaternion.identity);
                }
                else if (color.Equals(new Color32(0, 255, 255, 255)))
                {
                    Instantiate(pointLightPrefab, pos + Vector3.up * 1.5f, Quaternion.identity);
                }
                else if (color.Equals(new Color32(255, 0, 0, 255)))
                {                    
                    Instantiate(metalWallPrefab, pos, Quaternion.identity, transform);                    
                }
                else if (color.Equals(new Color32(255, 255, 0, 255)))
                {                    
                    Instantiate(windowPrefab, pos, Quaternion.identity, transform);                    
                }
                else if (color.Equals(new Color32(255, 0, 255, 255)))
                {
                    Instantiate(flashlightPrefab, pos + Vector3.up * 1f, Quaternion.identity);
                }
            }
        }
    }
